def checkPartitions(df: org.apache.spark.sql.DataFrame) = {
    println("Number of partitions used: " + df.rdd.getNumPartitions)
    println("Sample of objects per partitions")
    df.mapPartitions(iter => Seq(iter.size).iterator).show
}

