val sortedRows = ordersRDD.map(row => ((row.getString(0), row.getString(2)), row.getDouble(5)))
                      .groupByKey.map{case (key, iter) => (key, iter.sum)}
                      .sortBy(row => (row._1._1,  row._2), ascending = false)
                      .take(20)
for (row <- sortedRows) {
    println(row)
}

// Optimization Using reduceByKey
val sortedRows = ordersRDD.map(row => ((row.getString(0), row.getString(2)), row.getDouble(5)))
                          .reduceByKey(_+_)
                          .sortBy(pair => (pair._1._1,  pair._2), ascending = false)
                          .take(20)
for (row <- sortedRows) {
    println(row)
}

